import React, { useState } from "react";
import { List, InputNumber, Space } from "antd";
import { MessageOutlined, LikeOutlined, StarOutlined } from "@ant-design/icons";
import { useSelector, useDispatch } from "react-redux";

const listData = [
  {
    id: 1,
    name: "#",
    title: `Iphone 11`,
    description: "Iphone 11 is special in 2020 by the Apple",
    price: 1000,
  },
  {
    id: 2,
    name: "#",
    title: `Samsung note 10`,
    description: "Samsung note 10 is special in 2020 by the Samsung",
    price: 900,
  },
  {
    id: 3,
    name: "#",
    title: `Samsung note 10`,
    description: "Samsung note 10 is special in 2020 by the Samsung",
    price: 900,
  },
];

//Total price of products
var totalBill = 0;
listData.forEach((item) => {
  totalBill = totalBill + item.price;
});

const IconText = ({ icon, text }) => (
  <Space>
    {React.createElement(icon)}
    {text}
  </Space>
);
export default function Cart() {
  const dispatch = useDispatch();

  const [amount, setAmount] = useState(1);
  const list = useSelector((state) => state.list);
  list.

  return (
    <>
      <List
        itemLayout="vertical"
        size="large"
        pagination={{ pageSize: 3 }}
        dataSource={listData}
        footer={
          <h1>
            <b>Total Bill</b> {totalBill}
          </h1>
        }
        renderItem={(item) => (
          <List.Item
            key={item.title}
            actions={[
              <IconText
                icon={StarOutlined}
                text="156"
                key="list-vertical-star-o"
              />,
              <IconText
                icon={LikeOutlined}
                text="156"
                key="list-vertical-like-o"
              />,
              <IconText
                icon={MessageOutlined}
                text="2"
                key="list-vertical-message"
              />,
            ]}
            extra={
              <a name={item.name}>
                <img
                  width={150}
                  alt="logo"
                  src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
                />
              </a>
            }
          >
            <List.Item.Meta
              title={<a name={item.name}>{item.title}</a>}
              description={item.description}
            />
            {item.price}
            <div style={{ display: "flex" }}>
              <InputNumber min={0} placeholder="Số lượng" />
            </div>
          </List.Item>
        )}
      />
      {list.map((item) => (
        <h1>{item.description}</h1>
      ))}
    </>
  );
}
