import React, { useState } from "react";
import { Modal, Button, Form, Input, Select, InputNumber, message } from "antd";
import callApi from "../../api/ApiCaller";

export default function ModalForm() {
  const [visible, setVisible] = useState(false);
  const [isLoad, setIsLoad] = useState(false);

  const showModal = () => {
    setVisible(true);
  };

  const onFinish = async (value) => {
    await callApi("post", "products", value).catch((err) => console.log(err));
    setVisible(false);
    setIsLoad(false);
    message.success("Add new product success!");
  };
  const onCancel = () => {
    setVisible(false);
    set
  }

  return (
    <>
      <Button block type="primary" onClick={() => showModal()}>
        Add a Product
      </Button>
      <Modal
        closable={false}
        title="Add a product into the table"
        visible={visible}
        footer={null}
      >
        <Form
          initialValue={true}
          onFinish={onFinish}
          wrapperCol={{ span: 20 }}
          layout="horizontal"
        >
          <Form.Item
            rules={[{ required: true, message: "Please input product name!" }]}
            name="product"
          >
            <Input placeholder="Product" />
          </Form.Item>
          <Form.Item
            rules={[{ required: true, message: "Please input product cost!" }]}
            name="cost"
          >
            <InputNumber placeholder="Cost" />
          </Form.Item>
          <Form.Item
            rules={[{ required: true, message: "Please input description!" }]}
            name="description"
          >
            <Input placeholder="Description" />
          </Form.Item>
          <Form.Item
            rules={[
              { required: true, message: "Please input in or out stock!" },
            ]}
            name="stock"
          >
            <Select placeholder="Stock">
              <Select.Option value="In Stock">In Stock</Select.Option>
              <Select.Option value="Out Stock">Out Stock</Select.Option>
            </Select>
          </Form.Item>
          <Form.Item>
            <Button
              onClick={() => setIsLoad(true)}
              loading={isLoad}
              style={{ margin: 5 }}
              type="primary"
              htmlType="submit"
            >
              Add
            </Button>
            <Button onClick={() => onCancel()}>Cancel</Button>
          </Form.Item>
        </Form>
      </Modal>
    </>
  );
}
