import React from "react";
import { List, Space, Button } from "antd";
import { CloseOutlined } from "@ant-design/icons";
import { useSelector, useDispatch } from "react-redux";
import { incrementAmount, decrementAmount } from "../../action/List";

export default function Cart() {
  const IconText = ({ icon, text }) => (
    <Space>
      {React.createElement(icon)}
      {text}
    </Space>
  );
  const listData = useSelector((state) => state.list);
  const dispatch = useDispatch();

  //Total price of products
  let totalBill = 0;
  listData.forEach((item) => {
    totalBill = totalBill + item.price * item.amount;
  });

  const increment = (id) => {
    listData.forEach((item) => {
      if (item.id === id) {
        dispatch(incrementAmount(id));
      }
    });
  };
  const decrement = (id) => {
    listData.forEach((item) => {
      if (item.id === id) {
        dispatch(decrementAmount(id));
      }
    });
  };
  return (
    <List
      itemLayout="vertical"
      size="large"
      pagination={{ pageSize: 10 }}
      dataSource={listData}
      footer={
        <h1>
          <b>Total Bill</b> {totalBill} $
        </h1>
      }
      renderItem={(item) => (
        <List.Item
          key={item.title}
          actions={[
            <IconText icon={CloseOutlined} text="delete" />,
          ]}
          extra={
            <a href="https://github.com/">
              <img width={150} alt="logo" src={item.image} />
            </a>
          }
        >
          <List.Item.Meta title={item.title} description={item.description} />
          price {item.price}
          <div style={{ display: "flex" }}>
            <h3>Amount</h3>
            <Button style onClick={() => decrement(item.id)}>-</Button>
            <h1>{item.amount}</h1>
            <Button onClick={() => increment(item.id)}>+</Button>
          </div>
        </List.Item>
      )}
    />
  );
}
