import React, { useState } from "react";
import "./App.css";
import { Layout, Menu, Button, Avatar, Popconfirm,Badge } from "antd";
import "antd/dist/antd.css";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { QuestionCircleOutlined,UserOutlined,ShoppingCartOutlined } from "@ant-design/icons";
import ModalFormLogin from "./components/ModalFormLogin";
import ModalFormLogout from "./components/ModalFormSignin";
import Cart from "./components/cart/index";
import NotFound from "./components/notFound/notFound";
import Home from "./components/home/ProductsList";
import Index from "./components/Table/Index";

function App() {
  const routes = [
    {
      title: <h3>Products</h3>,
      path: "/",
      exact: true,
      main: () => <Home />,
    },
    {
      title: <h3>Contact</h3>,
      path: "/contact",
      main: () => <h1>contact</h1>,
    },
    {
      title: <h3>Table</h3>,
      path: "/table",
      main: () => <Index />,
    },
    {
      title: (
        <h3>
          <span>Cart</span>
          <Badge count={2}>
            <Avatar
              style={{ marginBottom: 10, marginLeft: 5 }}
              shape="square"
              icon={<ShoppingCartOutlined />}
            />
          </Badge>
        </h3>
      ),
      path: "/manager",
      main: () => <Cart />,
    },
    {
      title: "",
      path: "",
      main: () => <NotFound />,
    },
  ];
  const [isLogin, setIsLogin] = useState(true);
  const { Content, Footer } = Layout;

  const routerComponent = routes.map((route, index) => (
    <Route
      key={index}
      path={route.path}
      exact={route.exact}
      children={<route.main />}
    />
  ));

  const menuHeader = routes.map((route, index) => (
    <Menu.Item key={index}>
      <Link to={route.path}>{route.title}</Link>
    </Menu.Item>
  ));

  const login = () => (
    <>
      <ModalFormLogin />
      <ModalFormLogout />
    </>
  );
  const logout = () => (
    <div style={{ float: "right" }}>
      <Badge count={1}>
      <Avatar style={{ backgroundColor: '#87d068' }} icon={<UserOutlined />} />
      </Badge>
      <Popconfirm
        placement="bottomRight"
        onConfirm={() => setIsLogin(false)}
        title="Are you sure？"
        icon={<QuestionCircleOutlined style={{ color: "red" }} />}
      >
        <Button type="link">
          Logout
        </Button>
      </Popconfirm>
    </div>
  );

  return (
    <Router>
      <Layout className="layout">
        <Menu theme="light" mode="horizontal" defaultSelectedKeys={["0"]}>
          {menuHeader}
          {isLogin ? logout() : login()}
        </Menu>
        <Content>
          <Switch>{routerComponent}</Switch>
        </Content>
        <Footer style={{ textAlign: "center" }}>MY_CV@ using reactjs</Footer>
      </Layout>
    </Router>
  );
}

export default App;
