const initialState = []
const addList = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_LIST": {
      return [...state, action.payload];
    }
    case "INCREMENT_AMOUNT": {
      return [...state, state[action.payload].amount + 1];
    }
    case "DECREMENT_AMOUNT": {
      return [...state, action.payload];
    }
    default:
      return state;
  }
};

export default addList;
