import React from "react";
import { Affix, Button, Badge } from "antd";
import { ShoppingCartOutlined,createFromIconfontCN } from "@ant-design/icons";
import { useSelector, useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";
import { selectMenuX } from "../../action/SelectMenu";

export default function ButtonCart() {
  const dispatch = useDispatch();
  const listCart = useSelector((state) => state.list);
  const history = useHistory();
  const goToCart = () => {
    history.push("/cart");
    dispatch(selectMenuX(9));
  };
  const IconFont = createFromIconfontCN({
    scriptUrl: [
      '//at.alicdn.com/t/font_1788044_0dwu4guekcwr.js', // icon-javascript, icon-java, icon-shoppingcart (overrided)
      '//at.alicdn.com/t/font_1788592_a5xf2bdic3u.js', // icon-shoppingcart, icon-python
    ],
  });

  return (
    <Affix offsetTop={20}>
      <Badge count={listCart.length} showZero>

        <Button shape="circle" onClick={() => goToCart()} type="primary">
        <IconFont type="icon-shoppingcart" />
        </Button>
      </Badge>
    </Affix>
  );
}
