const initialState = {
  item: {},
  status: false,
}

const modalProducts = (state = initialState, action) => {
  switch (action.type) {
    case 'MODAL_PRODUCTS':
      return {
        item: action.payload,
        status: !initialState.status,
      };
    default:
      return state;
  }
};
export default modalProducts;
