const addList = (state = [], action) => {
  switch (action.type) {
    case "ADD_LIST": {
      return [...state, action.payload];
    }
    case "INCREMENT_AMOUNT": {
      return (
        state.forEach(element => {
          if (element.id === action.payload) {
            element.amount+=1
          }

        })
      )
    }
    default:
      return state;
  }
};


export default addList;

